    def popUpGetNewHotkey(self):
        self.w = QtWidgets.QMainWindow()
        self.ui = AnotherWindow()
        self.ui.setupUi(self.w)
        self.w.show()
        count = 0
        newHotkey = []
        done = False
        while not done:
            QApplication.processEvents()
            if self.w.isVisible() == False:
                break
            for key in KEYS:
                if keyboard.is_pressed(key) and key != "enter":
                    if len(newHotkey) > 0:
                        if newHotkey.count(key) == 0:
                            newHotkey.append(key)
                            count += 1
                            self.ui.changeText(newHotkey)
                            QApplication.processEvents()

                    else:
                        newHotkey.append(key)
                        count += 1
                        self.ui.changeText(newHotkey)
                        QApplication.processEvents()

                elif keyboard.is_pressed(key) and key == "enter":
                    done = True
                    break

                if count == 3:
                    break
            if count == 3:
                break

            time.sleep(.1)
        return newHotkey



